mapper.select:
select t1.tzw_item_id,t1.tzw_item_name,t1.tzw_item_price,t1.tzw_item_num, t1.tzw_item_status,t1.tzw_item_picture,t1.tzw_item_desc,t1.tzw_item_createDate,t1.tzw_item_updateDate, t1.tzw_item_jifen,t1.tzw_item_choujiang,t1.tzw_item_content,t1.tzw_item_new,t1.tzw_item_jingpai,tzw_item_type,tzw_item_leibie from tzw_item t1
WHERE t1.tzw_item_name LIKE concat('%',#{lname},'%')
limit #{cpage},#{size}

mapper.insert:
insert into tzw_jifen (tzw_jifen_createDate,
                             tzw_jifen_item_id,
                             tzw_jifen_yue,
                             tzw_jifen_xianjin,
                             tzw_jifen_num
                ) values (
                #{tzw_jifen_createDate},
                #{tzw_jifen_item_id},
                #{tzw_jifen_yue},
                #{tzw_jifen_xianjin},
                #{tzw_jifen_num}
                );

mapper.select:
SELECT * from tzw_item  where tzw_item_name=#{tzw_item_name}
                         AND tzw_item_num=#{tzw_item_num}
                         AND tzw_item_price=#{tzw_item_price}

mapper.update:
UPDATE tzw_item SET tzw_item_name=#{tzw_item_name}, tzw_item_num=#{tzw_item_num}, tzw_item_status=#{tzw_item_status},
tzw_item_picture=#{tzw_item_picture},
tzw_item_updateDate=#{tzw_item_updateDate}, tzw_item_desc=#{tzw_item_desc}, tzw_item_jifen=#{tzw_item_jifen}, tzw_item_jingpai=#{tzw_item_jingpai}, tzw_item_choujiang=#{tzw_item_choujiang}, tzw_item_content=#{tzw_item_content}, tzw_item_new=#{tzw_item_new} WHERE tzw_item_id=#{tzw_item_id}
