mapper.select:
select t1.tzw_item_id,t1.tzw_item_name,t1.tzw_item_price,t1.tzw_item_num, t1.tzw_item_status,t1.tzw_item_picture,t1.tzw_item_desc,t1.tzw_item_createDate,t1.tzw_item_updateDate, t1.tzw_item_jifen,t1.tzw_item_choujiang,t1.tzw_item_jingpai from tzw_item t1
WHERE t1.tzw_item_name LIKE concat('%',#{lname},'%')
limit #{cpage},#{size}

mapper.insert:
insert into tzw_choujiang (tzw_choujiang_item_id,
                             tzw_choujiang_houNum,
                             tzw_choujiang_preNum,
                             tzw_choujiang_createDate
                ) values (
                #{tzw_choujiang_item_id},
                #{tzw_choujiang_houNum},
                #{tzw_choujiang_preNum},
                #{tzw_choujiang_createDate}
                );

insert into tzw_jifen (tzw_jifen_createDate,
                             tzw_jifen_item_id,
                             tzw_jifen_num
                ) values (
                #{tzw_jifen_createDate},
                #{tzw_jifen_item_id},
                #{tzw_jifen_num}
                );

insert into tzw_jingpai (tzw_jingpai_createDate,
                             tzw_jingpai_item_id,
                             tzw_jingpai_num
                ) values (
                #{tzw_jingpai_createDate},
                #{tzw_jingpai_item_id},
                #{tzw_jingpai_num}
                );

mapper.select:
SELECT * from tzw_item  where tzw_item_name=#{tzw_item_name}
                         AND tzw_item_num=#{tzw_item_num}
                         AND tzw_item_price=#{tzw_item_price}
